import React, { Component } from 'react';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';

export default class CreateAppoinment extends Component {
    constructor(props){
        super(props);

        //this.onChangeFirstname=this.onChangeFirstname.bind(this);
        this.onChangeLastname=this.onChangeLastname.bind(this);
        this.onChangeAddress=this.onChangeAddress.bind(this);
        this.onChangeTimeslot=this.onChangeTimeslot.bind(this);
        this.onChangedate=this.onChangedate.bind(this);
        this.onSubmit=this.onSubmit.bind(this);


            this.state={
            Firstname:"",
            Lastname:"",
            Address:"",
            Timeslot:0,
            date: new Date(),
            users:[]
        }
    }

   // componentDidMount(){
   //     this.setState({
     //       users:['test user'],
       //     Firstname:'test user'
        //})  
    //}

    onChangeFirstname = (e) => {
        this.setState({
            Firstname: e.target.value
        });
    }
    onChangeLastname(e){
        this.setState({
            Lastname: e.target.value
        });
    }
    onChangeAddress(e){
        this.setState({
            Address: e.target.value
        });
    }
    onChangeTimeslot(e){
        this.setState({
            Timeslot: e.target.value
        });
    }
    onChangedate(date){
        this.setState({
            date: date
        });
    }
    onSubmit(e){
        e.preventDefault();
        const appoinment={
         Firstname: this.state.Firstname,
         Lastname:this.state.Lastname,
         Address:this.state.Address,
         Timeslot:this.state.Timeslot,
         date:this.state.date   
        }
        console.log(appoinment);

        window.location='/';
    }
    render(){
        return(
            <div>
      <h3>Create New Appoinment  Log</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Firstname: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.Firstname}
              onChange={this.onChangeFirstname}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
          <label>Lastname: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.Lastname}
              onChange={this.onChangeLastname}
              />
        </div>
        <div className="form-group"> 
          <label>Address: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.Address}
              onChange={this.onChangeAddress}
              />
        </div>
        <div className="form-group">
          <label>Timeslot (in hours): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.Timeslot}
              onChange={this.onChangeTimeslot}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Create appoinment Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
        )
    }

}
